import React from 'react';

import '../styles/stylesheet.css';
import '../styles/header_EU.css';
import '../styles/landing_page.css';
import '../styles/faq.css';
import '../styles/activities.css';

import lozanoImage from '../styles/images/Lozano.png';
import gregNewsroom from '../styles/images/greg_newsroom.jpg';
import emmyImage from '../styles/images/emmy.jpg';
import jessieImage from '../styles/images/jessie_esp.png';

const NewsItem = ({ image, title, link, date, description }) => (
    <div className="col-md-4 news-item">
        <div className="news-image">
            <img src={image} alt="News Image" />
        </div>
        <div className="news-content">
            <h2 className="news-title"><a className="news-link" target="_blank" rel="noreferrer" href={link}>{title}</a></h2>
            <p className="news-date">{date}</p>
            <p className="news-description">{description}</p>
        </div>
    </div>
);

const Home = ({ selectedLanguage }) => {
    const newsItems = texts[selectedLanguage].newsItems
    const texts = {
        en: {
            newsItems: [
                {
                    image: gregNewsroom,
                    title: 'UTRGV Energy & U first live show garners about 3,000 attendees',
                    link: 'https://www.utrgv.edu/newsroom/2023/01/27/utrgvs-energy-u-first-live-show-garners-about-3000-attendees.htm',
                    date: 'January 27, 2023',
                    description: "RIO GRANDE VALLEY, TEXAS More than 3,000 elementary students and the general public witnessed the first live performance of UTRGV's “Energy & U” spectacle, a show filled with explosions, bubbles and lots of learning and fun.",
                },

                { 
                    image: emmyImage, 
                    title: 'Energy & U receives the Rocky Mountain Emmy Award for Best Educational Program', 
                    link: 'https://www.valleycentral.com/news/local-news/utrgv-engineering-professor-and-team-win-lone-star-emmy/', 
                    date: 'January 7, 2023', 
                    description: 'The University of Texas Rio Grande Valley professor of mechanical engineering Karen Lozano and her team won a Lone Star Emmy on Nov. 12.', 
                },

                { 
                    image: jessieImage, 
                    title: 'UTRGV Energy & U hosts school for solar car competition', 
                    link: 'https://texasborderbusiness.com/3000-attend-energy-u-first-live-show/', 
                    date: 'January 27, 2023', 
                    description: 'EDINBURG, Texas More than 3,000 elementary students and the general public witnessed the first live performance of UTRGVs “Energy & U” spectacle, a show filled with explosions, bubbles and lots of learning and fun.', 
                },
            ],
        },
        es: {
            newsItems: [
                {
                    image: gregNewsroom,
                    title: 'UTRGV Energy & U first live show garners about 3,000 attendees',
                    link: 'https://www.utrgv.edu/newsroom/2023/01/27/utrgvs-energy-u-first-live-show-garners-about-3000-attendees.htm',
                    date: 'January 27, 2023',
                    description: "RIO GRANDE VALLEY, TEXAS More than 3,000 elementary students and the general public witnessed the first live performance of UTRGV's “Energy & U” spectacle, a show filled with explosions, bubbles and lots of learning and fun.",
                },

                { 
                    image: emmyImage, 
                    title: 'Energy & U receives the Rocky Mountain Emmy Award for Best Educational Program', 
                    link: 'https://www.valleycentral.com/news/local-news/utrgv-engineering-professor-and-team-win-lone-star-emmy/', 
                    date: 'January 7, 2023', 
                    description: 'The University of Texas Rio Grande Valley professor of mechanical engineering Karen Lozano and her team won a Lone Star Emmy on Nov. 12.', 
                },

                { 
                    image: jessieImage, 
                    title: 'UTRGV Energy & U hosts school for solar car competition', 
                    link: 'https://texasborderbusiness.com/3000-attend-energy-u-first-live-show/', 
                    date: 'January 27, 2023', 
                    description: 'EDINBURG, Texas More than 3,000 elementary students and the general public witnessed the first live performance of UTRGVs “Energy & U” spectacle, a show filled with explosions, bubbles and lots of learning and fun.', 
                },
            ],
        },
    };

const t = texts[selectedLanguage];

        return(
        <div className = "BODY" >
            <div className="container-fluid TOP">
                <main className="row middle-section text-center">
                    <div className="col">
                        <div className="title" id="siteContent">Learn, Explore, Play!</div>
                        <div className="subtext">Welcome to the Energy & U Activities website, Learn and discover anywhere you are</div>

                        <div className="button_container d-flex justify-content-center align-items-center mt-3"> {/* Added flexbox utilities */}
                            <div className="heartbeat">
                                <a className="btn btn-link first-link" href="E_U_2.html">EXPERIMENTS</a>
                            </div>
                        </div>
                    </div>
                </main>

                <section className="row lozano-section">
                    <div className="col lozanoImg text-center">
                        <img className="lozano" src={lozanoImage} alt="Lozano Image" />
                        <div className="tooltip">Click on the Experiments Button to Play & Learn</div>
                        <div className="tooltipDos"></div>
                    </div>
                </section>
            </div>

            <section className="container-fluid bottomTwo">
                <div className="row justify-content-center">
                    <div className="col text-center">
                        <div className="titleBottom">What is Energy & U?</div>
                        <div className="subtitleBottom">Welcome to "Energy and U!" Brought to life by top minds from the University of Minnesota, and enriched with the generous support of the National Science Foundation, this program is ready to light up the Valley. A fun-filled exploration of energy for young learners, "Energy and U" is all about inspiring the next generation to care for our wonderful planet. It's time to power up your curiosity and spark our sense of responsibility. Join us in the adventure!</div>
                        <div className="embed-responsive-video embed-responsive-16by9 video-container-large">
                            <iframe className="embed-responsive-item" src="https://www.youtube.com/embed/H2x6e8dBnO8?rel=0" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>
                        </div>
                    </div>
                </div>
            </section>

            <section className="container-fluid news-section">
                <div className="row">
                    <div className="col text-center">
                        <div className="titleBottomThree">Energy and U Highlights</div>
                    </div>
                </div>
                <div className="row news-container">
                    {newsItems.map((item, index) => (
                        <NewsItem key={index} {...item} />
                    ))}
                </div>
            </section>

            <footer className="py-4 bg-dark text-white mt-5">
                <div className="container">
                    <p className="m-0 text-center">&copy; 2022 Energy & U. All Rights Reserved.</p>
                </div>
            </footer>
        </div >
    );
}

export default Home;
