import React from 'react';
import '../styles/stylesheet.css';
import '../styles/header_EU.css';
import '../styles/landing_page.css';
import '../styles/faq.css';
import '../styles/activities.css';

const Game = ({ selectedLanguage }) => {
  const backgroundStyle = {
    backgroundImage: `url(${require('../styles/images/website-background.jpg')})`, // Use require to ensure correct path resolution
    backgroundSize: 'cover',
  };

  return (
    <div style={backgroundStyle}>
      <div className="container mt-5">
        <div className="row justify-content-center">
          <div className="col-md-10">
            <div className="embed-responsive embed-responsive-16by9 shadow-lg rounded">
              <iframe className="embed-responsive-item" src="https://cdn.htmlgames.com/embed.js?game=ClassicBubbleShooter&amp;bgcolor=white" allowFullScreen></iframe>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Game;
