import React, { useState } from 'react';
import '../styles/stylesheet.css';
import '../styles/header_EU.css';
import '../styles/landing_page.css';
import '../styles/faq.css';
import '../styles/activities.css';

const Navbar = () => {
  const [selectedLanguage, setSelectedLanguage] = useState('en');

  const switchLanguage = (lang) => {
    setSelectedLanguage(lang);
    // Later, you can add functionality to switch the entire site's language here.
  }
  return (
    <header className="header_EU">
        <div className="col left-section-EU">
            <div className="containerLogo">
                <img className="rotate-center" src="images/energy-and-u-white.png" alt="Logo Image" />
            </div>
        </div>
        <div className="middle-section-header">
            <span className="middleTitle">Energy & U</span>
        </div>
        <div className="col right-section-EU text-end">
            <div className="dropdown d-md-none">
                <button className="btn green-dropdown-btn responsive-dropdown-btn" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="false">
                    Menu <i className="fas fa-chevron-down"></i>
                </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                    <a className="dropdown-item" href="/game">Activities</a>
                    <a className="dropdown-item" href="/faq">FAQ</a>
                    <a className="dropdown-item" href="#" data-lang="en">ENG</a>
                    <a className="dropdown-item" href="#" data-lang="es">ESP</a>
                </div>
            </div>

            <div className="d-none d-md-inline-block">
                <a className="Activities d-inline-block" href="/game">Activities</a>
                <a className="FAQ d-inline-block" href="/faq">FAQ</a>
                <div className="language-switcher d-inline-block">
                    <a href="#" className="language-link selected" data-lang="en">ENG</a>
                    <span className="language-divider"> / </span>
                    <a href="#" className="language-link" data-lang="es">ESP</a>
                </div>
            </div>
        </div>
    </header>
  );
}

export default Navbar;
